mapper.update:
UPDATE comment
SET comment = #{comment}
where commentNum = #{commentNum}

mapper.select:
select u.userEmail
from user u, comment c
where u.userId=c.userId and c.userId = #{userId}

select userEmail
from user
where userId=#{userId}

select u.userEmail, c.comment, c.commentTime, b.boardNum
from user u, comment c, board b
where u.userId=c.userId and b.boardNum = #{boardNum}

select count(*) comment
from comment
where boardNum = #{boardNum}
