mapper.update:
UPDATE comment
SET comment = #{comment}, commentTime=sysdate
where commentNum = #{commentNum}

update commment
set comment='삭제되었습니다'
where commentNum = #{commentNum}

mapper.select:
SELECT boardNum, commentNum, userEmail, comment, commentTime
FROM comment
where boardNum = #{boardNum}

select count(*)
from comment
where boardNum = #{boardNum}
